//создание в базе данных пользователя и сессии
const createUserAndSession = async (req, res) => {
  //из req.body забираем все данные, которые прописывали в инпутах
  const {
    fio,
    login,
    password,
    role
  } = req.body;
  try {
    if (role === 'babushkagram') {
      //создание пароля
      const hashedPassword = await bcrypt.hash(password, 10);
      //cоздание в бд пользователя
      const user = await Grandparent.create({
        fio,
        login,
        password: hashedPassword,
        help: true
      });
      //создание сессии с данными: id, name, role
      req.session.user = {
        id: user.id,
        name: user.fio,
        role,
        login: user.login,
        help: user.help
      };
      req.session.save(() => {
        res.redirect('/babushkagram');
      });
    }
    ;
    //тоже самое для внука
    if (role === 'vnukogram') {
      const hashedPassword = await bcrypt.hash(password, 10);
      const user = await Grandchild.create({
        fio,
        login,
        password: hashedPassword
      });
      req.session.user = {
        id: user.id,
        name: user.fio,
        role,
        login: user.login
      };
      req.session.save(() => {
        res.redirect('/vnukogram');
      });
    }
    ;
  } catch (error) {
    console.log(error);
    render(Signup, {
      error
    }, res);
  }
};
